import React from 'react';
import { useState } from 'react';
import axios from 'axios'



const Connexion = () => {

    const [nom, setNom] = useState("")
    const [mdp, setMdp] = useState("")
    const miova = (e) => {
        setMdp(e.target.value)
    }


    const mampiditra = async (req, res, error) => {
        try {
            const alefa = await axios.post('http://localhost:8000/login', { nom, mdp }).then((response) => {
                console.log(response.data);
            }).catch((error) => {
                console.log(error);
            })
        } catch (error) {

        }

    }

    return (
        <div>
            <label htmlFor="">login:</label>    <input type="text" value={nom} onChange={(e) => { setNom(e.target.value) }} /> <br />
            <label htmlFor="">mot de passe:</label>    <input type="text" value={mdp} onChange={miova} /><br />
            <button onClick={mampiditra} >Connexion</button>
        </div>
    );
};

export default Connexion;
