import React from 'react';
import { useState } from 'react';
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css";

const Connexion = () => {
  const [nom, setNom] = useState("");
  const [mdp, setMdp] = useState("");

  const miova = (e) => {
    setMdp(e.target.value);
  }

  const mampiditra = async (req, res, error) => {
    try {
      const alefa = await axios.post('http://localhost:8000/login', { nom, mdp }).then((response) => {
        setNom("");
        setMdp("");
      }).catch((error) => {
        console.log(error);
      })
    } catch (error) {
    }
  }

  return (
    <div className="connexion-container " style={{background}}> {/* Ajout de la classe CSS 'bg-gray' pour le fond gris  f0f2f5*/}
      <Container>
        <Row className="justify-content-center">
          <Col md={6}>
            <Form className="bg-white p-3"> {/* Ajout de la classe CSS 'bg-white' pour le formulaire en blanc et 'p-3' pour l'espacement */}
              <Form.Group controlId="formBasicNom">
                <Form.Control
                  type="text"
                  placeholder="Adresse e-mail ou numéro de tél."
                  value={nom}
                  onChange={(e) => setNom(e.target.value)}
                />
              </Form.Group>
              <br />
              <Form.Group controlId="formBasicPassword">
                <Form.Control
                  type="password"
                  placeholder="Mot de passe"
                  value={mdp}
                  onChange={(e) => setMdp(e.target.value)}
                /> <br />
              </Form.Group>
              <div className="text-center">
                <Button variant="primary" onClick={mampiditra} block>
                  Se connecter
                </Button>
              </div>
            </Form>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Connexion;
