import React from 'react';
import { useState } from 'react';
import axios from 'axios'



const Connexion = () => {

    const [nom, setNom] = useState("")
    const [mdp, setMdp] = useState("")
    const miova = (e) => {
        setMdp(e.target.value)
    }


    const mampiditra = async (req, res, error) => {
        try {
            const alefa = await axios.post('http://localhost:8000/login', { nom, mdp }).then((response) => {
                setNom("")
                setMdp("")
            }).catch((error) => {
                console.log(error);
            })
        } catch (error) {

        }

    }

    return (
        // <div>
        //     <label htmlFor="">login:</label>    <input type="text" value={nom} onChange={(e) => { setNom(e.target.value) }} /> <br />
        //     <label htmlFor="">mot de passe:</label>    <input type="text" value={mdp} onChange={miova} /><br />
        //     <button onClick={mampiditra} >Connexion</button>
        // </div>


<Container>
<Row className="justify-content-center">
  <Col md={6}>
    <div className="text-center">
      <h1>Se connecter</h1>
    </div>
    <Form>
      <Form.Group controlId="formBasicNom">
        <Form.Label>Nom d'utilisateur</Form.Label>
        <Form.Control
          type="text"
          placeholder="Saisissez votre nom d'utilisateur"
          value={nom}
          onChange={(e) => setNom(e.target.value)}
        />
      </Form.Group>

      <Form.Group controlId="formBasicPassword">
        <Form.Label>Mot de passe</Form.Label>
        <Form.Control
          type="password"
          placeholder="Saisissez votre mot de passe"
          value={mdp}
          onChange={(e) => setMdp(e.target.value)}
        />
      </Form.Group>

      <Button variant="primary" onClick={mampiditra} block>
        Connexion
      </Button>
    </Form>
  </Col>
</Row>
</Container>
    );
};

export default Connexion;
